////////////////////////////////////////////////////////////////////////////////
// GetOptimizedSortTable
// Returns an optimized sort path for the given sort data.
// Currently only returns simple stores until the transaction list performance
// is increased.


struct TablePair {
	PassTable* table;
	int numThreads;
};

const TablePair LargeTables[8] = {
	{ sort_256_8_index_simple_large, 256 },
	{ sort_128_8_key_simple_large, 128 },
	{ sort_256_8_single_simple_large, 256 },
	{ sort_256_8_multi_2_simple_large, 256 },
	{ sort_256_8_multi_3_simple_large, 256 },
	{ sort_256_8_multi_4_simple_large, 256 },
	{ sort_256_8_multi_5_simple_large, 256 },
	{ sort_256_8_multi_6_simple_large, 256 }
};

const TablePair SmallTables[8] = {
	{ sort_256_8_index_simple_small, 256 },
	{ sort_128_8_key_simple_small, 128 },
	{ sort_128_8_single_simple_small, 128 },
	{ sort_256_8_multi_2_simple_small, 256 },
	{ sort_256_8_multi_3_simple_small, 256 },
	{ sort_256_8_multi_4_simple_small, 256 },
	{ sort_256_8_multi_5_simple_small, 256 },
	{ sort_256_8_multi_6_simple_small, 256 }
};

SortTable GetOptimizedSortTable(sortData_t data) {
	bool s = data->numElements <= 1000000;
	int valCount = data->valueCount + 1;
	int numBits = data->endBit - data->firstBit - 1;

	TablePair t = s ? SmallTables[valCount] : LargeTables[valCount];
	SortTable table;
	std::copy(t.table[numBits], t.table[numBits] + 6, table.pass);
	table.numSortThreads = t.numThreads;
	table.valuesPerThread = 8;
	table.useTransList = false;

	return table;
}
